mapper.insert:
insert into tbl_member
(
	memberId, memberPw, memberName, memberAge, memberGender, memberEmail, 
	memberZipcode, memberAddress, memberAddressDetail
)
values
(
	#{memberId}, #{memberPw}, #{memberName}, #{memberAge}, #{memberGender}, #{memberEmail},
	 #{memberZipcode}, #{memberAddress}, #{memberAddressDetail}
 )

mapper.update:
UPDATE TABLE_MEMBER 
SET	NAME = #{name}, AGE = #{age}
WHERE ID = #{id}
